package com.getyoteam.budamind.fragmentimport android.content.Intentimport android.os.Bundleimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport androidx.fragment.app.Fragmentimport com.getyoteam.budamind.MyApplicationimport com.getyoteam.budamind.Prefsimport com.getyoteam.budamind.Rimport com.getyoteam.budamind.activity.WalletActivityimport com.getyoteam.budamind.utils.Utilsimport kotlinx.android.synthetic.main.fragment_catalo.*class CatalogFragment() : Fragment() {    var prefs: Prefs? = null    override fun onCreateView(        inflater: LayoutInflater, container: ViewGroup?,        savedInstanceState: Bundle?    ): View? {        // Inflate the layout for this fragment        return inflater.inflate(R.layout.fragment_catalo, container, false)    }    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {        super.onViewCreated(view, savedInstanceState)        prefs = Prefs(requireContext())        layBalance.setOnClickListener {            val intent = Intent(requireContext(), WalletActivity::class.java)            startActivity(intent)        }        try {            val a = " $"+"CHI"            val blnc = Utils.formatBal(MyApplication.prefs!!.myBalance.toBigInteger())            tvBalance.text =  blnc +a        }catch (e :Exception){            e.printStackTrace()        }    }}