package com.getyoteam.budamind.interfacesimport androidx.room.*import com.getyoteam.budamind.Model.CourseListModel@Daointerface CourseDao {  @Query("SELECT * FROM courselistmodel")  fun getAll(): List<CourseListModel>  @Query("SELECT * FROM courselistmodel WHERE courseId IN (:courseId)")  fun loadAllByIds(courseId: IntArray): List<CourseListModel>  @Insert(onConflict = OnConflictStrategy.REPLACE)  fun insertAll(courseListModel: CourseListModel)  @Delete  fun delete(courseListModel: CourseListModel)  @Query("DELETE FROM courselistmodel")  fun deleteAll()}