package com.getyoteam.budamind.interfacesimport androidx.room.*import com.getyoteam.budamind.Model.SoundListModel@Daointerface SoundDao {  @Query("SELECT * FROM soundlistmodel")  fun getAll(): List<SoundListModel>  @Query("SELECT * FROM soundListModel WHERE soundId IN (:soundId)")  fun loadAllByIds(soundId: IntArray): List<SoundListModel>  @Insert(onConflict = OnConflictStrategy.REPLACE)  fun insertAll(soundListModel: SoundListModel)  @Insert(onConflict = OnConflictStrategy.REPLACE)  fun insertAll(soundListModel: ArrayList<SoundListModel>)  @Delete  fun delete(soundListModel: SoundListModel)  @Query("DELETE FROM soundListModel")  fun deleteAll()}